// LinkedIn Post AI Enrichment Merger
// Use this in a Code node in n8n

// Get input data
const inputData = $input.all();

// Validate input structure
if (!inputData || inputData.length < 2) {
  console.log('Invalid input: Expected array with at least 2 items');
  return inputData; // Return original data if structure is wrong
}

try {
  // Extract AI enrichment and webhook data
  const aiEnrichment = inputData[0]?.json?.output;
  const webhookData = inputData[1]?.json?.body;

  // Validate required data exists
  if (!aiEnrichment || !webhookData?.payload) {
    console.log('Missing AI enrichment or webhook payload data');
    return inputData;
  }

  // Transform the original payload
  const originalPayload = webhookData.payload;

  // Rename id to objectID and merge with AI enrichment
  const transformedPayload = {
    ...originalPayload,
    objectID: originalPayload.id, // Rename id to objectID
    aiEnriched: {
      primaryTopic: aiEnrichment.primaryTopic,
      contentType: aiEnrichment.contentType,
      themes: aiEnrichment.themes || [],
      language: aiEnrichment.language || { name: "Unknown", code: "unknown" },
      aiGenerated: aiEnrichment.aiGenerated || { isLikely: false, confidence: 0 },
      confidence: aiEnrichment.confidence || 0
    },
    savedOn: new Date().getTime()
  };

  // Remove the original id field since we renamed it to objectID
  delete transformedPayload.id;

  // Create the final output structure
  const result = {
    body: {
      ...webhookData,
      payload: transformedPayload
    },
    webhookUrl: webhookData.webhookUrl || inputData[1]?.json?.webhookUrl,
    executionMode: webhookData.executionMode || inputData[1]?.json?.executionMode
  };

  console.log('âœ… Successfully transformed LinkedIn post with AI enrichment');
  console.log(`ðŸ“Š Primary Topic: ${aiEnrichment.primaryTopic}`);
  console.log(`ðŸŽ¯ Content Type: ${aiEnrichment.contentType}`);
  console.log(`ðŸ·ï¸ Themes: ${aiEnrichment.themes?.join(', ')}`);

  return [result];

} catch (error) {
  console.error('âŒ Error transforming data:', error.message);
  return inputData; // Return original data on error
}
